// Bardzo prosty kod React ‚Äì styl: "sta≈ºysta z YouTube"

import React, { useEffect, useState } from 'react';
import axios from 'axios';

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [filter, setFilter] = useState('all');
  const [showAddForm, setShowAddForm] = useState(false); // Kontroluje widoczno≈õƒá formularza dodawania
  const [editingTask, setEditingTask] = useState(null); // ID zadania w edycji
  const [editTitle, setEditTitle] = useState(''); // Tytu≈Ç w edycji
  const [editDescription, setEditDescription] = useState(''); // Opis w edycji

  useEffect(() => {
    getTasks();
  }, []);

  function getTasks() {
    axios.get('http://localhost:8080/api/tasks')
      .then(res => setTasks(res.data))
      .catch(err => console.log(err));
  }

  function addTask() {
    if (!title) return;
    axios.post('http://localhost:8080/api/tasks', {
      title: title,
      description: description
    })
    .then(() => {
      setTitle('');
      setDescription('');
      setShowAddForm(false); // Ukryj formularz po dodaniu
      getTasks();
    });
  }

  function deleteTask(id) {
    axios.delete(`http://localhost:8080/api/tasks/${id}`)
      .then(() => getTasks());
  }

  function toggleTask(id) {
    axios.patch(`http://localhost:8080/api/tasks/${id}/toggle`)
      .then(() => getTasks());
  }

  function clearDone() {
    const doneTasks = tasks.filter(t => t.done);
    doneTasks.forEach(t => {
      axios.delete(`http://localhost:8080/api/tasks/${t.id}`)
        .then(() => getTasks());
    });
  }

  // Rozpocznij edycjƒô zadania
  function startEdit(task) {
    setEditingTask(task.id);
    setEditTitle(task.title);
    setEditDescription(task.description);
  }

  // Zapisz zmiany w zadaniu
  function saveEdit() {
    if (!editTitle) return;
    axios.put(`http://localhost:8080/api/tasks/${editingTask}`, {
      title: editTitle,
      description: editDescription,
      done: tasks.find(t => t.id === editingTask).done // Zachowaj obecny status
    })
    .then(() => {
      setEditingTask(null);
      setEditTitle('');
      setEditDescription('');
      getTasks();
    });
  }

  // Anuluj edycjƒô
  function cancelEdit() {
    setEditingTask(null);
    setEditTitle('');
    setEditDescription('');
  }

  const filteredTasks = tasks.filter(task => {
    if (filter === 'done') return task.done;
    if (filter === 'notdone') return !task.done;
    return true;
  });

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto', padding: '20px' }}>
      <h1>To-Do App</h1>

      <div>
        <button onClick={() => setShowAddForm(!showAddForm)}>
          {showAddForm ? 'Anuluj' : 'Dodaj nowe zadanie'}
        </button>
        <button onClick={clearDone}>Wyczy≈õƒá wykonane</button>
      </div>

      <div style={{ marginTop: '10px' }}>
        <span>Filtr: </span>
        <button onClick={() => setFilter('all')}>Wszystkie</button>
        <button onClick={() => setFilter('done')}>‚úî Wykonane</button>
        <button onClick={() => setFilter('notdone')}>‚úò Niewykonane</button>
      </div>

      {/* Formularz dodawania - pokazuje siƒô tylko po klikniƒôciu */}
      {showAddForm && (
        <div style={{ marginTop: '20px', border: '2px solid #007bff', padding: '15px', borderRadius: '5px', backgroundColor: '#f8f9fa' }}>
          <h2>‚ûï Dodaj nowe zadanie</h2>
          <div>
            <label>Tytu≈Ç: </label>
            <input value={title} onChange={(e) => setTitle(e.target.value)} />
          </div>
          <div>
            <label>Opis: </label>
            <input value={description} onChange={(e) => setDescription(e.target.value)} />
          </div>
          <button onClick={addTask}>Dodaj zadanie</button>
          <button onClick={() => setShowAddForm(false)}>Anuluj</button>
        </div>
      )}

      <div style={{ marginTop: '20px' }}>
        {filteredTasks.map(task => (
          <div key={task.id} style={{ border: '1px solid #ccc', padding: '10px', marginBottom: '10px' }}>
            {/* Tryb edycji */}
            {editingTask === task.id ? (
              <div style={{ backgroundColor: '#fff3cd', padding: '10px', borderRadius: '5px' }}>
                <div>
                  <label>Tytu≈Ç: </label>
                  <input value={editTitle} onChange={(e) => setEditTitle(e.target.value)} />
                </div>
                <div>
                  <label>Opis: </label>
                  <input value={editDescription} onChange={(e) => setEditDescription(e.target.value)} />
                </div>
                <button onClick={saveEdit}>üíæ Zapisz</button>
                <button onClick={cancelEdit}>‚ùå Anuluj</button>
              </div>
            ) : (
              /* Tryb normalny */
              <div>
                <h3>{task.done ? '‚úÖ' : 'üü¶'} {task.title}</h3>
                <p>Opis: {task.description}</p>
                <button onClick={() => toggleTask(task.id)}>
                  {task.done ? '‚úò Oznacz jako niewykonane' : '‚úî Oznacz jako wykonane'}
                </button>
                <button onClick={() => startEdit(task)}>‚úèÔ∏è Edytuj</button>
                <button onClick={() => deleteTask(task.id)}>üóë Usu≈Ñ</button>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
